import React, {Fragment} from 'react'
import Planet from './planet'

class Panets extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      planets: [
        {
          name: "Mercúrio",
          description: "Mercúrio é o menor[nota 1][nota 2] e mais interno planeta do Sistema Solar, orbitando o Sol a cada 87,969 dias terrestres. A sua órbita tem a maior excentricidade e o seu eixo apresenta a menor inclinação em relação ao plano da órbita dentre todos os planetas do Sistema Solar.",
          img_url: "https://upload.wikimedia.org/wikipedia/commons/thumb/d/d9/Mercury_in_color_-_Prockter07-edit1.jpg/280px-Mercury_in_color_-_Prockter07-edit1.jpg",
          link: "https://pt.wikipedia.org/wiki/Merc%C3%BArio_(planeta)"
        },
        {
          name: "Vênus",
          description: "Vênus é o segundo planeta do Sistema Solar em ordem de distância a partir do Sol, orbitando-o a cada 224,7 dias. Recebeu seu nome em homenagem à deusa romana do amor e da beleza Vénus, equivalente a Afrodite ",
          img_url: "https://upload.wikimedia.org/wikipedia/commons/thumb/0/02/OSIRIS_Mars_true_color.jpg/280px-OSIRIS_Mars_true_color.jpg",
          link: "https://pt.wikipedia.org/wiki/V%C3%A9nus_(planeta)"
        }
      ]
    }
  }
  render(){
    return (
      <Fragment>
        <h3>Planet List</h3>
        <button>Show Message</button>
        <hr/>
        <Planet name=""
                description=""
                img_url=""
                link=""

        />
        <Planet name="Vênus"
                description="Vênus é o segundo planeta do Sistema Solar em ordem de distância a partir do Sol, orbitando-o a cada 224,7 dias. Recebeu seu nome em homenagem à deusa romana do amor e da beleza Vénus, equivalente a Afrodite "
                img_url="https://upload.wikimedia.org/wikipedia/commons/thumb/0/02/OSIRIS_Mars_true_color.jpg/280px-OSIRIS_Mars_true_color.jpg"
                link="https://pt.wikipedia.org/wiki/V%C3%A9nus_(planeta)"
        />
      </Fragment>
    )
  }
}

export default Planets
